<?php

namespace TCG\Bundle\CMF;


use Symfony\Component\HttpFoundation\Response;
use TCG\Bundle\CMF\Service\Account;
use TCG\Bundle\Twig\Component\TwigHttpExec;

abstract class CMFExec extends TwigHttpExec
{
    use PrivateTrait;
    /**
     * @var Account;
     */
    private $_account;

    protected $permission;

    public function __invoke()
    {
        $this->permission = $this->getRoute();
        if ($this->needAuthenticate()) {
            $response = $this->authenticate();
            if ($response) {
                return $response;
            }
        }
        try {
            if ($this->_account) {
                $this->permission();
            }
            return parent::__invoke();
        } catch (\Exception $e) {
            if ($e->getCode() == CMFException::CODE_ACCOUNT_PERMISSION_DENY) {
                return $this->processPermissionDenyRequest();
            } else {
                throw $e;
            }
        }
    }


    public function setAccount(Account $account)
    {
        $this->_account = $account;
    }


    public function getAccount()
    {
        return $this->_account;
    }

    protected function needAuthenticate()
    {
        return true;
    }


    protected function permission()
    {
        if ($this->permission && $this->_account) {
            if (!$this->_account->hasPermission($this->permission)) {
                throw new CMFException("用户权限不足", CMFException::CODE_ACCOUNT_PERMISSION_DENY);
            }
        }
    }

    public function render($template_path = 'index/index.html.twig', array $context = array())
    {
        $context['account'] = $this->_account;
        return parent::render($template_path, $context); // TODO: Change the autogenerated stub
    }

    /**
     * 检查account
     * @return null|Response
     */
    abstract protected function authenticate();

    /**
     * @return bool|mixed|null|string|Database\MySQL\Model\User
     */
    protected function getUser()
    {
        $user = $this->servicePassport()
            ->login();
        if ($user) {
            $account = $this->serviceUser()
                ->account($user);
            $this->setAccount($account);
        }
        return $user;
    }


    protected function processPermissionDenyRequest()
    {
        $request = $this->getRequest();

    }

}